import React, { Component } from 'react'
import ReactEcharts from 'echarts-for-react'
var option = {
    // title: {
    //     text: 'ECharts 入门示例'
    // },
    // tooltip: {},
    // legend: {
    //     data: ['销量']
    // },
    // xAxis: {
    //     data: ["衬衫", "羊毛衫", "雪纺衫", "裤子", "高跟鞋", "袜子"]
    // },
    // yAxis: {},
    // series: [{
    //     name: '销量',
    //     type: 'bar',
    //     data: [5, 20, 36, 10, 10, 20]
    // }]

    xAxis: {
        type: 'category',
        data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
    },
    yAxis: {
        type: 'value'
    },
    tooltip: {
        trigger: 'item',
        formatter: '数值: {c} '
    },
    series: [{
        data: [120, 200, 150, 80, 70, 110, 130],
        type: 'pie',
        symbol: 'triangle',
        symbolSize: 20,
        lineStyle: {
            color: 'green',
            width: 4,
            type: 'dashed'
        },
        itemStyle: {
            borderWidth: 3,
            borderColor: 'yellow',
            color: 'blue'
        }
    }]
};

class Echarts extends Component {
    render() {
        return (
            <div id='main'
                style={{
                    width: '100%',
                    height: '100%'
                }}
            >
                <h2 style={{
                    textAlign: 'center',
                }}>Echarts仪表盘</h2>
                <ReactEcharts
                    option={option}
                    notmerge={'true'}
                    lazyupdate={'false'}>
                </ReactEcharts>
            </div>
        )
    }
}
export default Echarts
