import React, { Component } from 'react'
import { Form, Icon, Input, Button, Checkbox } from 'antd';

class Login extends Component {
    setLocal() {
        let info = JSON.stringify({ err: 0, msg: 'ok', token: '12313', rootIds: ['1', '0', '2-0', '2-0', '2-3'] })
        localStorage.setItem('info', info)
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <div>
                <Form onSubmit={this.handleSubmit} className="login-form">
                    <Form.Item>
                        {getFieldDecorator('username', {
                            rules: [{ required: true, message: '请输入账号' }],
                        })(
                            <Input
                                prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,1)' }} />}
                                type="text"
                                placeholder="Username"
                            />,
                        )}
                    </Form.Item>
                    <Form.Item>
                        {getFieldDecorator('password', {
                            rules: [{ required: true, message: '请填写密码' }],
                        })(
                            <Input
                                prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,1)' }} />}
                                type="password"
                                placeholder="Password"
                            />,
                        )}
                    </Form.Item>
                    <Form.Item>
                        <Button type="primary" style={{margin: 10 }}>
                            登录
                        </Button>
                        {getFieldDecorator('remember', {
                            valuePropName: 'checked',
                            initialValue: true,
                        })(<Checkbox>记住密码</Checkbox>)}

                    </Form.Item>
                </Form>
                <button onClick={this.setLocal.bind(this)}>登录</button>
            </div>
        )
    }
}
export default Form.create({ name: 'normal_login' })(Login)
