import axios from '../utils/axios'

export const UserLogin = async (userName,passWord) => {
    let url = '/user/v1/admin/user/login'
    let result = await axios.post(url, { userName,passWord })
    if (result.err===0) {
        console.log(result)
        return result
    }else{
        throw result
    }
}
