import { Menu, Dropdown, Icon } from 'antd';
import React, { Component } from 'react'

export default class HeaderDropdown extends Component {
    logout(){
        console.log(key)
    }
    render() {
        return (
            <Dropdown overlay={(
                <Menu>
                    <Menu.Item key="0">
                        个人中心
                    </Menu.Item>
                    <Menu.Item key="1">
                        我的消息
                    </Menu.Item>
                    <Menu.Divider />
                    <Menu.Item key="3" onClick={(e) => { this.logout(e) }}>
                        退出
                  </Menu.Item>
                </Menu>
            )}>
                <div style={{ marginRight: 40 }} className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                    {localStorage.getItem('userName')} <Icon type="down" />
                </div>
            </Dropdown>
        )
    }
}
