import { Menu, Dropdown, Icon, message, Popconfirm } from 'antd';
import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'

class HeaderDropdown extends Component {
    logout(e) {
        console.log(e)
        switch (e.key) {
            case '0':
                message.success('跳转个人中心', 2)
                break;
            case '1':
                message.success('跳转我的消息', 2)
                break;
        }
    }
    render() {
        return (
            <Dropdown overlay={(
                <Menu onClick={(e) => { this.logout(e) }}>
                    <Menu.Item key="0">
                        个人中心
                    </Menu.Item>
                    <Menu.Item key="1">
                        我的消息
                    </Menu.Item>
                    <Menu.Divider />
                    <Menu.Item key="3" >
                        <Popconfirm
                            title="确定退出？"
                            icon={<Icon type="question-circle-o" style={{ color: 'red' }} />}
                            onConfirm={() => {
                                message.success('退出成功', 1, () => {
                                    this.props.history.push('/login')
                                })
                            }}
                        >
                            退出
                        </Popconfirm>
                    </Menu.Item>
                </Menu>
            )}>
                <div style={{ marginRight: 40 }} className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                    {localStorage.getItem('userName')} <Icon type="down" />
                </div>
            </Dropdown>
        )
    }
}

export default withRouter(HeaderDropdown)
