import React, { Component } from 'react'
import { Form, Icon, Input, Button, Checkbox, Card } from 'antd';

class Login extends Component {
    setLocal() {
        let info = JSON.stringify({ err: 0, msg: 'ok', token: '12313', rootIds: ['1', '0', '2-0', '2-0', '2-3'] })
        localStorage.setItem('info', info)
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <Card title="登录信息" extra={<a href="#">注册</a>} style={{ width: 400 }}>
                <Form.Item label="用户名">
                    {getFieldDecorator('username：', {
                        rules: [{ required: true, message: '请输入账号' }],
                    })(
                        <Input
                            prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,1)' }} />}
                            placeholder="Username"
                        />,
                    )}
                </Form.Item>
                <Form.Item label="密码：">
                    {getFieldDecorator('password', {
                        rules: [{ required: true, message: '请填写密码' }],
                    })(
                        <Input
                            prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,1)' }} />}
                            type="password"
                            placeholder="Password"
                        />,
                    )}
                </Form.Item>
                <Form.Item>
                    <Button type="primary" onClick={this.setLocal.bind(this)}>
                        登录
                    </Button>
                    {getFieldDecorator('remember', {
                        valuePropName: 'checked',
                        initialValue: true,
                    })(<Checkbox  style={{ margin: 10 }} >记住密码</Checkbox>)}

                </Form.Item>
            </Card>
        )
    }
}
export default Form.create({ name: 'normal_login' })(Login)
