/** 
 * @顶部导航
 * 点击登出，退出界面
 * 从状态管理内获取数据（过滤出状态为未读的信息数量），显示小标，未读信息，点击跳转页面
 * 
 * */  

import { Menu, Dropdown, Icon, message, Popconfirm } from 'antd';
import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { UserLogout } from '../../api/user'
import { clearStorage } from '../../utils/storage'
const menuData = [
    { path: '/admin/userinfo', name: '个人信息', icon: 'user' },
    { path: '/admin/info', name: '个人设置', icon: 'user' },
    { path: '/login', name: '退出登录', icon: 'user' }
]

class HeaderDropdown extends Component {
    toggle(e) {
        switch (e.key) {
            case '/admin/userinfo':
                message.success('跳转个人中心', 2)
                break;
            case '/admin/info':
                message.success('跳转我的消息', 2,()=>{
                    this.props.history.push(e.key)
                })
                break;
            case '/login':
                message.success('退出成功', 1, this.logout(e.key))
                break;
        }
    }
    logout(url) {
        UserLogout()
            .then(data => {
                clearStorage()
                this.props.history.replace(url)
            })
    }
    renderNav = (menuData) => {
        return (
            <Menu onClick={(e) => { this.toggle(e) }}>
                {menuData.map((item) => {
                    return (
                        <Menu.Item key={item.path}>
                            <span>
                                <Icon type={item.icon}></Icon>
                                <span>{item.name}</span>
                            </span>
                        </Menu.Item>
                    )
                })}
            </Menu>
        )
    }
    render() {
        return (
            <Dropdown overlay={this.renderNav(menuData)}>
                <div style={{ marginRight: 40 }} className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                    {localStorage.getItem('userName')} <Icon type="down" />
                </div>
            </Dropdown>
        )
    }
}

export default withRouter(HeaderDropdown)
