import Prevstate from './state'
import * as types from './action-type'


export default (state = Prevstate, { type, params }) => {
    let newData = JSON.parse(JSON.stringify(state))

    switch (type) {
        case types.READ_ALL:
            let arr = newData.list.map((item) => {
                return item.bool = false
            })
            newData.list = arr
            break;
        case types.READ_ONE:
            newData.list[params].bool = false
            break;
        case types.START:
            newData.status=true
            break;
        case types.END:
            newData.status=false
            break;
        default:
    }
    return newData
}
