import React, { Component, } from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import actionCreator from '../../store/actionCreator'
import { List, Typography } from 'antd';

class Info extends Component {
    render() {
        let { list ,changebool} = this.props
        return (
            <div>
                <h3 style={{ marginBottom: 16 }}>个人消息
                <button>全部标记为已读</button>
                </h3>
                <List
                bordered
                dataSource={list}
                renderItem={(item,index) => (
                    <List.Item>
                        <Typography.Text mark>{item.type}</Typography.Text> {item.info}
                        {item.bool ? <button onClick={() => {
                            changebool(index)
                        }}>标记为已读</button> : <span>已读</span>}
                    </List.Item>
                )}
            />
            </div>
        )
    }
}
export default connect(state => state, (dispatch) => {
    return bindActionCreators(actionCreator, dispatch)
})(Info)

//个人消息渲染页面
// 1. 获取数据存到stote
// 2. 渲染
// 3. 根据状态确定是否标记，以及已读按钮
// 4. 点击按钮，设置为已读——状态切换过程中，有加载显示
// 5. 全部已读
// 6. 个人信息处显示小标
// 7. 一键清空
