import React, { Component } from 'react'
import { Form, Icon, Input, Button, Checkbox, Card } from 'antd';
import styles from './login.model.less'

class Login extends Component {
    setLocal() {
        let info = JSON.stringify({ err: 0, msg: 'ok', token: '12313', rootIds: ['1', '0', '2-0', '2-0', '2-3'] })
        localStorage.setItem('info', info)
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <div className='login'>
                <Card className='cart' title="登录信息" extra={<a href="#">注册</a>} style={{ width: 400 }}>
                    <Form.Item label="用户名" >
                        {getFieldDecorator('username：', {
                            rules: [{ required: true, message: '用户名为手机号', pattern: /^[1][3,4,5,7,8][0-9]{9}$/ }],
                        })(
                            <Input
                                prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,1)' }} />}
                                placeholder="Username"
                            />,
                        )}
                    </Form.Item>
                    <Form.Item label="密码：">
                        {getFieldDecorator('password', {
                            rules: [{ required: true, message: '密码至少包含 数字和英文，长度6-20', pattern: /^(?![0-9]+$)(?![a-zA-Z]+$)[0-9A-Za-z]{6,20}$/ }],
                        })(
                            <Input
                                prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,1)' }} />}
                                type="password"
                                placeholder="Password"
                            />,
                        )}
                    </Form.Item>
                    <Form.Item>
                        <Button type="primary" onClick={() => {
                            console.log(this.props.getFieldsValue)
                            this.setLocal.bind(this)
                        }
                        }>
                            登录
                    </Button>
                        {getFieldDecorator('remember', {
                            valuePropName: 'checked',
                            initialValue: true,
                        })(<Checkbox style={{ marginLeft: 20 }}>记住密码</Checkbox>)}

                    </Form.Item>
                </Card>
            </div>
        )
    }
}
export default Form.create({ name: 'normal_login' })(Login)
